  ButtonContainer,
} from 'react-native';

import { GoogleTranslateAPI } from 'react-i18n';
import GoogleTranslate from 'react-i18n-google-translate';

const Translation = () => {
  const [translated, setTranslated] = useState(null);

  const { text, message } = await GoogleTranslate(
    text,
    message
  );

  if (translated) {
    setTranslated(translated);
  }

  return (
    <MessageBlock id="translation-message">{message}</MessageBlock>
  );
};

export default Translation;
```
